import React, { useEffect, useState } from 'react';
import Chart from 'chart.js/auto';
import { register } from 'chart.js'; // Import only the register function from 'chart.js'
import ChartDataLabels from 'chartjs-plugin-datalabels';
import 'chartjs-adapter-date-fns';
register(ChartDataLabels);
import { format, startOfWeek, endOfWeek, startOfMonth, endOfMonth } from 'date-fns';

const FinancialTransactionChart = ({ transactions }) => {
  const [chart, setChart] = useState(null);

  useEffect(() => {
    if (transactions.length > 0) {
      const aggregatedData = aggregateData(transactions);

      const ctx = document.getElementById('financialChart').getContext('2d');
      const newChart = new Chart(ctx, {
        type: 'line',
        data: {
          labels: aggregatedData.labels,
          datasets: [
            {
              label: 'Total Transactions',
              data: aggregatedData.data,
              backgroundColor: 'rgba(75, 192, 192, 0.2)',
              borderColor: 'rgba(75, 192, 192, 1)',
              borderWidth: 2,
              pointRadius: 4,
              pointBackgroundColor: 'rgba(75, 192, 192, 1)',
              pointBorderColor: '#fff',
              pointHoverRadius: 6,
              pointHoverBackgroundColor: 'rgba(75, 192, 192, 1)',
            },
          ],
        },
        options: {
          scales: {
            x: {
              type: 'time',
              time: {
                unit: 'day', // Adjust the time unit based on your data
              },
              title: {
                display: true,
                text: 'Date',
              },
            },
            y: {
              beginAtZero: true,
              title: {
                display: true,
                text: 'Transaction Amount',
              },
            },
          },
        },
      });

      setChart(newChart);
    }
  }, [transactions]);

  const aggregateData = (data) => {
    const aggregatedByDay = data.reduce((result, transaction) => {
      const date = new Date(transaction.timestamp);
      const formattedDate = format(date, 'MM/dd/yyyy');
      const existingIndex = result.labels.findIndex((label) => label === formattedDate);

      if (existingIndex !== -1) {
        result.data[existingIndex] += transaction.amount;
      } else {
        result.labels.push(formattedDate);
        result.data.push(transaction.amount);
      }

      return result;
    }, { labels: [], data: [] });

    return aggregatedByDay;
  };

  return <canvas id="financialChart" width="800" height="400"></canvas>;
};

export default FinancialTransactionChart;
