import React, { useState } from 'react';
import "../index.css"

const BankAccountCard = ({ bankAccount }) => {
  const [expanded, setExpanded] = useState(false);

  const handleCardClick = () => {
    setExpanded(!expanded);
  };

  return (
    <div className="bank-account-card" onClick={handleCardClick}>
      <div className="bank-account-card_card-header">
        <h3>{bankAccount.owner}'s {bankAccount.accountType} Account</h3>
        <p className="bank-account-card">{bankAccount.balance > 0 
          ? `Balance: $${bankAccount.balance}` 
          : `Balance: - $${Math.abs(bankAccount.balance)}` }</p>
      </div>
      {expanded && (
        <div className="transaction-list">
          <h4>Transactions:</h4>
          <ul>
            {bankAccount.transactions.map((transaction) => (
              <li key={transaction.id}>
                {new Date(transaction.timestamp).toLocaleString()}: ${transaction.amount}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default BankAccountCard;
