// MonthlyFinancialChart.js
import React, { useEffect, useState } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import { Chart, registerables } from 'chart.js/auto';
import { format } from 'date-fns';

// Register necessary plugins
Chart.register(...registerables);

const MonthlyFinancialChart = ({ transactions }) => {
    const [chart, setChart] = useState(null);
    const [selectedDate, setSelectedDate] = useState(new Date());
    const [aggregatedData, setAggregatedData] = useState({ labels: [], data: [], minLoss: 0, maxIncome: 0 });

    useEffect(() => {
      // Check if there's an existing chart
      if (chart) {
        // Destroy the existing chart
        chart.destroy();
      }

      if (transactions.length > 0) {
        const data = aggregateData(transactions);
        setAggregatedData(data);

        const ctx = document.getElementById('monthlyFinancialChart').getContext('2d');
        const newChart = new Chart(ctx, {
        type: 'bar',
        data: {
          labels: aggregatedData.labels,
          datasets: [
            {
              label: 'Monthly Transactions',
              data: aggregatedData.data,
              backgroundColor: aggregatedData.data.map((amount) => (amount >= 0 ? 'rgba(75, 192, 192, 0.2)' : 'rgba(255, 99, 132, 0.2)')),
              borderColor: aggregatedData.data.map((amount) => (amount >= 0 ? 'rgba(75, 192, 192, 1)' : 'rgba(255, 99, 132, 1)')),
              borderWidth: 1,
            },
          ],
        },
        options: {
          scales: {
            x: {
              title: {
                display: true,
                text: 'Month',
              },
            },
            y: {
              beginAtZero: true,
              suggestedMin: aggregatedData.minLoss,
              suggestedMax: aggregatedData.maxIncome,
              title: {
                display: true,
                text: 'Transaction Amount',
              },
            },
          },
        },
      });

      setChart(newChart);
    }
  }, [transactions, selectedDate]);

  const aggregateData = (data) => {
    const aggregatedByMonth = data.reduce(
      (result, transaction) => {
        const date = new Date(transaction.timestamp);
        const formattedMonth = format(date, 'MM/yyyy');

        // Check if the transaction is in the selected year
        if (date.getFullYear() === selectedDate.getFullYear()) {
          const existingIndex = result.labels.findIndex((label) => label === formattedMonth);

          if (existingIndex !== -1) {
            result.data[existingIndex] += transaction.amount;
          } else {
            result.labels.push(formattedMonth);
            result.data.push(transaction.amount);
          }

          // Update the maximum income and minimum loss
          if (transaction.amount >= 0) {
            result.maxIncome = Math.max(result.maxIncome, transaction.amount);
          } else {
            result.minLoss = Math.min(result.minLoss, transaction.amount);
          }
        }

        return result;
      },
      { labels: [], data: [], maxIncome: 0, minLoss: 0 }
    );

    return aggregatedByMonth;
  };

  const handleDateChange = (date) => {
    setSelectedDate(date);
  };

  return (
    <div>
      <DatePicker selected={selectedDate} onChange={handleDateChange} dateFormat="MM/yyyy" showMonthYearPicker />
      <canvas id="monthlyFinancialChart" width="800" height="400"></canvas>
    </div>
  );
};

export default MonthlyFinancialChart;
