import React, { useEffect, useState } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import { Chart, registerables } from 'chart.js/auto';
import { format, startOfWeek, endOfWeek, startOfMonth, endOfMonth } from 'date-fns';

// Register necessary plugins
Chart.register(...registerables);

const DailyTransactionChart = ({ transactions }) => {
  const [chart, setChart] = useState(null);
  const [selectedDate, setSelectedDate] = useState(new Date());

  useEffect(() => {
    // Check if there's an existing chart
    if (chart) {
      // Destroy the existing chart
      chart.destroy();
    }

    if (transactions.length > 0) {
      const aggregatedData = aggregateData(transactions);

      const ctx = document.getElementById('financialChart').getContext('2d');
      const newChart = new Chart(ctx, {
        type: 'bar',
        data: {
          labels: aggregatedData.labels,
          datasets: [
            {
              label: 'Daily Transactions',
              data: aggregatedData.data,
              backgroundColor: aggregatedData.data.map((amount) => (amount >= 0 ? 'rgba(75, 192, 192, 0.2)' : 'rgba(255, 99, 132, 0.2)')),
              borderColor: aggregatedData.data.map((amount) => (amount >= 0 ? 'rgba(75, 192, 192, 1)' : 'rgba(255, 99, 132, 1)')),
              borderWidth: 1,
            },
          ],
        },
        options: {
          scales: {
            x: {
              title: {
                display: true,
                text: 'Date',
              },
            },
            y: {
              beginAtZero: true,
              title: {
                display: true,
                text: 'Transaction Amount',
              },
            },
          },
        },
      });

      setChart(prevChart => newChart);
    }
  }, [transactions, selectedDate]);

  const aggregateData = (data) => {
    const aggregatedByDay = data.reduce(
      (result, transaction) => {
        const date = new Date(transaction.timestamp);
        const formattedDate = format(date, 'MM/dd/yyyy');

        // Check if the transaction is in the selected month
        // Check if the transaction is on the selected day
if (date.getMonth() === selectedDate.getMonth() && date.getFullYear() === selectedDate.getFullYear() && date.getDate() === selectedDate.getDate()) {

          const existingIndex = result.labels.findIndex((label) => label === formattedDate);

          if (existingIndex !== -1) {
            result.data[existingIndex] += transaction.amount;
          } else {
            result.labels.push(formattedDate);
            result.data.push(transaction.amount);
          }
        }

        return result;
      },
      { labels: [], data: [] }
    );

    return aggregatedByDay;
  };


  const handleDateChange = (date) => {
    setSelectedDate(date);
  };

  return (
    <div className='financialChart'>
      <DatePicker className="financialChart__datePicker"  selected={selectedDate} onChange={handleDateChange} dateFormat="MM/yyyy" showMonthYearPicker />
      <canvas id="financialChart" width="800" height="400"></canvas>
    </div>
  );
};



export default DailyTransactionChart;
