import "../index.css";
import { useState } from "react";
import { useOutletContext } from "react-router-dom";
import logo from "../images/Logo-dark-background.png";

function Login() {
  // Access the login function passed as context
  const login = useOutletContext();
  const [formData, setFormData] = useState({
    username: "",
    password: "",
  });

  function handleChange(e) {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  }

  // Create a function that calls the login function when the form is submitted
  function handleLogin(e) {
    e.preventDefault();
    login();
  }

  return (
    <div className="Login">
      <div className="Login_logo">
        <img src={logo} alt="Titan Financial" />
      </div>
      <div class="Login_h1">
        <h1>Titan Capital Financial</h1>
      </div>
      <div className="Login__form">
        <form onSubmit={handleLogin}>
          <div>
            <input
              id="username"
              type="text"
              name="username"
              placeholder="Username"
              value={formData.username}
              onChange={handleChange}
            />
          </div>
          <div>
            <input
              id="password"
              type="password"
              name="password"
              placeholder="Password"
              value={formData.password}
              onChange={handleChange}
            />
          </div>
          <button className="login"type="submit">Login</button>
        </form>
      </div>
    </div>
  );
}

export default Login;
