import React, { useEffect, useState } from 'react';
import { Chart, registerables } from 'chart.js/auto';
import ChartDataLabels from 'chartjs-plugin-datalabels';
import { format, startOfMonth, endOfMonth } from 'date-fns';

Chart.register(...registerables);
ChartDataLabels(Chart);

const FinancialTransactionChart = ({ transactions }) => {
  const [chart, setChart] = useState(null);
  const [selectedMonth, setSelectedMonth] = useState(new Date());

  useEffect(() => {
    if (chart) {
      chart.destroy();
    }

    if (transactions.length > 0) {
      const aggregatedData = aggregateData(transactions);

      const ctx = document.getElementById('financialChart').getContext('2d');
      const newChart = new Chart(ctx, {
        type: 'bar',
        data: {
          labels: aggregatedData.labels,
          datasets: [
            {
              label: 'Total Transactions',
              data: aggregatedData.data,
              backgroundColor: 'rgba(75, 192, 192, 0.2)',
              borderColor: 'rgba(75, 192, 192, 1)',
              borderWidth: 2,
            },
          ],
        },
        options: {
          scales: {
            x: {
              type: 'time',
              time: {
                unit: 'day',
                tooltipFormat: 'MM/DD/YYYY',
              },
              title: {
                display: true,
                text: 'Date',
              },
            },
            y: {
              beginAtZero: true,
              title: {
                display: true,
                text: 'Transaction Amount',
              },
            },
          },
        },
      });

      setChart(newChart);
    }
  }, [transactions, selectedMonth]);

  const aggregateData = (data) => {
    const filteredData = data.filter((transaction) => {
      const transactionDate = new Date(transaction.timestamp);
      return (
        transactionDate >= startOfMonth(selectedMonth) && transactionDate <= endOfMonth(selectedMonth)
      );
    });

    const aggregatedByDay = filteredData.reduce(
      (result, transaction) => {
        const date = new Date(transaction.timestamp);
        const formattedDate = format(date, 'MM/DD/YYYY');
        const existingIndex = result.labels.findIndex((label) => label === formattedDate);

        if (existingIndex !== -1) {
          result.data[existingIndex] += transaction.amount;
        } else {
          result.labels.push(formattedDate);
          result.data.push(transaction.amount);
        }

        return result;
      },
      { labels: [], data: [] }
    );

    return aggregatedByDay;
  };

  return (
    <div>
      <input
        type="month"
        value={format(selectedMonth, 'yyyy-MM')}
        onChange={(e) => setSelectedMonth(new Date(e.target.value))}
      />
      <canvas id="financialChart" width="800" height="400"></canvas>
    </div>
  );
};

export default FinancialTransactionChart;
